ifMode.script:
// add validation handler to the HTML form
htmlForm.getBeforeValidation().push(function() {
var valid = true;

// validate that if a disposition has been selected, all the fields associated with that disposition have
also been filled out
jq('#disposition .field-error').first().html('').hide(); // hide any previous error

if (jq('#disposition select:first').val() != '' &&
(jq('#disposition select:visible').is(function() { return jq(this).val() == ''; })
|| jq('#disposition input:visible').is(function() { return jq(this).val() == ''; }))) {
jq('#disposition .field-error').first().append('

').show();
valid = false;
}
return valid;
});

jq(function() {

// return appointment date can never be before encounter date
var setMinReturnVisitDate = function () {

var returnVisitDate = getValue('return-visit-date.value');

getField('return-visit-date.value').datepicker('option', 'minDate',
getField('encounterDate.value').datepicker('getDate'));

if (returnVisitDate && returnVisitDate != getValue('return-visit-date.value')) {
setValue('return-visit-date.value',''); // reset return visit date if change to encounter date makes it
invalid
alert('

');
}
}

getField('encounterDate.value').change(setMinReturnVisitDate);

setMinReturnVisitDate();

jq('.medication-checkbox > input:checkbox').each(function(idx, it){
if(jq(it).not(':checked')){
jq(it).closest("td").siblings().find(":input").each(function(i, el){
jq(el).attr("disabled", "disabled");
});
}

jq(it).change(function(){
if(jq(this).is(':checked')){
jq(this).closest("td").siblings().find(":input").each(function(i, el){
jq(el).removeAttr("disabled");
});
} else {
jq(this).closest("td").siblings().find(":input").each(function(i, el){
jq(el).val('').attr("disabled", "disabled");
});
}
});
});

jq('#disposition select:first').change(function() {
jq('#disposition .field-error').first().html('').hide(); // hide any previous error if disposition is
changed
});

});
