ifMode.script:
jq(document).ready(function() {
    // add handler validation to the HTML form
    htmlForm.getBeforeValidation().push(function() {
        var valid = true;

        // validate that if a disposition has been selected, all the fields associated with that disposition have also been filled out
        jq('#disposition .field-error').first().html('').hide(); // hide any previous error

        if (jq('#disposition select:first').val() != '' &&
            (jq('#disposition select:visible').is(function() { return jq(this).val() == ''; })
            || jq('#disposition input:visible').is(function() { return jq(this).val() == ''; }))) {
                jq('#disposition .field-error').first().append('').show();
                valid = false;
        }
        return valid;
    });

    jq('#disposition select:first').change(function() {
        jq('#disposition .field-error').first().html('').hide(); // hide any previous error if disposition is changed
    });

    // handlers for next and submit buttons, see nextAndSubmitButtons.js
    setUpNextAndSubmitButtons();
});

jq(function() {

    var hasValue = function(element) {
        return jq(element).find('.baby-name input').val();
    };



    var hideOtherBabies = function() {
        jq('#baby-2').hide();
        jq('#baby-3').hide();
        jq('#baby-4').hide();
    };

    if (!hasValue('#baby-2') && !hasValue('#baby-3') && !hasValue('#baby-4')) {
        hideOtherBabies();
        jq('#show-more-babies-button').show();
    }

    jq('#show-more-babies-button').click(function() {
        jq('.baby').show();
        jq('#show-more-babies-button').hide();
        jq('#show-less-babies-button').show();
    });

    jq('#show-less-babies-button').click(function() {
        hideOtherBabies();
        jq('#show-less-babies-button').hide();
        jq('#show-more-babies-button').show();
    });

})

htmlform.script:
jq(document).ready( function() {

   var displayGestationalAge = function(deliveryDate, lastEddDate) {
        if ( deliveryDate ) {
            // the estimatedLMP
            const tmpDate = new Date(lastEddDate);
            tmpDate.setDate(tmpDate.getDate() - 280);
            var gestationalDays = daysBetweenUTCDates(deliveryDate, tmpDate);
            const gestAgeWeeks = Math.floor(gestationalDays / 7);
            const gestAgeRemainderDays = gestationalDays % 7;
            jq("#calculated-gestational").show();
            jq("#calculated-gestational-age-value").text(gestAgeWeeks + " " +
                (gestAgeRemainderDays ? gestAgeRemainderDays + "/7 " : " ") +
                '');
        }
    }
    const options = { weekday: 'long', year: 'numeric', month: 'short', day: 'numeric' };
    const monthOption = { month: 'short'};
    const lastEDD = '';
    const lastEnteredEDD = '';

    var lastEddDate = null;
    if ( lastEDD ) {
        lastEddDate = new Date(lastEDD);
    }
    var lastEnteredEDDDate = null;
    if ( lastEnteredEDD ) {
        lastEnteredEDDDate = new Date(lastEnteredEDD);
    }
    const encDate = '';
    var currentEncDate = new Date();
    if ( encDate ) {
        currentEncDate = new Date(+encDate);
    }
    if ( currentEncDate && lastEddDate && lastEnteredEDDDate ) {
        var daysBetween = daysBetweenUTCDates(currentEncDate, lastEnteredEDDDate);
        // the Estimated delivery date observation should have been entered within the previous 10 months( approx 305 days) before the MCH Delivery form encounter date
        if ( daysBetween <= 305 ) {
            jq("#calculated-edd").text(lastEddDate.getDate() + " " + new Intl.DateTimeFormat(undefined, monthOption).format(lastEddDate) + " " + lastEddDate.getFullYear());
            var deliveryDateField = jq("#calculated-edd");
            if ( deliveryDateField && deliveryDateField.text()) {
                    displayGestationalAge(new Date(deliveryDateField.text()), lastEddDate);
            }
            //  when a delivery date is entered calculate the gestational age from the EDD
            jq("#deliveryDate input[type='hidden']").change(function () {
                const deliveryDateValue = this.value;
                //the deliveryDate is a string with the following format YYYY-MM-DD
                if ( deliveryDateValue ) {
                    const deliveryDate = new Date(deliveryDateValue);
                    if ( deliveryDate ) {
                        displayGestationalAge(deliveryDate, lastEddDate);
                    }
                }
            });
        }
    }


});
