htmlform.script:
  jq(document).ready(function() {

    const vitalsSignsEncounterType = '4fb47712-34a6-40d2-8ed3-e153abbd25b7';
    const systolicBPConceptUuid = '3ce934fa-26fe-102b-80cb-0017a47871b2';
    const diastolicBPConceptUuid = '3ce93694-26fe-102b-80cb-0017a47871b2';
    const hrConceptUuid = '3ce93824-26fe-102b-80cb-0017a47871b2';
    const temperatureConceptUuid = '3ce939d2-26fe-102b-80cb-0017a47871b2';
    const respiratoryRateConceptUuid = '3ceb11f8-26fe-102b-80cb-0017a47871b2';
    const o2SatConceptUuid = '3ce9401c-26fe-102b-80cb-0017a47871b2';
    const pregnancyHistoryConstruct = '163588AAAAAAAAAAAAAAAAAAAAAAAAAAAAAA';
    const obGynEncounterType= 'd83e98fd-dc7b-420f-aa3f-36f648b4483d';
    const typeOfVisit = '164181AAAAAAAAAAAAAAAAAAAAAAAAAAAAAA';
    const intakeVisit = '164180AAAAAAAAAAAAAAAAAAAAAAAAAAAAAA';
    const whProcedurePerformed="1651AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA";
    const whProcedures="57755670-c183-494b-bd5e-723e0978c3e3";
    const whSterilizationProcedures = "46cbbdd9-9cc2-42c6-8ecb-4dbe6ea01354";

    const lastPeriodDateConcept = '';
    const dueDateConcept = '';
    const trimAtEnrollConcept = '';
    const firstTrimConcept = '';
    const secondTrimConcept = '';
    const thirdTrimConcept = '';
    const gravidaConcept = '';
    const paraConcept = '';
    const abortusConcept = '';
    const livingConcept = '';
    const menstruationConcept = '';
    const regularMenstrConcept = '';
    const irregularMenstrConcept = '';
    const regularTxt = '';
    const irregularTxt = '';
    const visitDiagConcept = '';
    const diagConcept = '';
    const dysmenorConcept = '';
    const durationMenstCycleConcept = '';
    const durationMensesConcept = '';
    const ageMenarcheConcept = '';
    const age1stSexualInterConcept = '';
    const numSexPartnersConcept = '';
    const pregOrderConcept = '';
    const deliveryTypeConcept = '';
    const pregOutcomeConcept = '';

    const contextPath = window.location.href.split('/')[3];
    const apiBaseUrl =  "/" + contextPath + "/ws/rest/v1";
    const options = { weekday: 'long', year: 'numeric', month: 'short', day: 'numeric' };
    const monthOption = { month: 'short'};

    const patientUuid = '';
    let currentEncounterDate = new Date();
    let lookupEncDate = '';
    if ( lookupEncDate ) {
        currentEncounterDate = new Date(+lookupEncDate);
    }

    let serverEncounterDate = '';

    const visitUuid = '';

    jq.getJSON(apiBaseUrl + "/visit/" + visitUuid, {
        v: 'custom:(uuid,id,startDatetime,encounters:(uuid,display,encounterDatetime,encounterType:(uuid,name),obs:(uuid,display,concept:(uuid,display),obsDatetime,value)))'
    },
    function( data ){
        if (data.encounters && data.encounters.length > 0) {
              for (let index=0; index < data.encounters.length; ++index) {
                let encounter = data.encounters[index];
                if (encounter.encounterType.uuid === vitalsSignsEncounterType) {
                  // we found a VitalsSigns encounter on this visit
                  let encounterDate = new Date(encounter.encounterDatetime);
                  let month = new Intl.DateTimeFormat(undefined, monthOption).format(encounterDate);
                  jq("#lastVitalsEncounterDate").text(encounterDate.getDate() + "-" + month + "-" + encounterDate.getFullYear());
                  jq("#lastVitalsEncounterTime").text(encounterDate.toLocaleTimeString());

                  if (encounter.obs && encounter.obs.length > 0) {
                    jq('#lastVitals').show();
                    for (let j=0; j < encounter.obs.length; ++j) {
                      let obs = encounter.obs[j];
                      if (obs.concept.uuid === systolicBPConceptUuid) {
                        jq("#last_bp_systolic").text(obs.value);
                        jq("#last_bp_systolic").removeClass("emptyValue").addClass("value");
                      } else if (obs.concept.uuid === diastolicBPConceptUuid) {
                        jq("#last_bp_diastolic").text(obs.value);
                        jq("#last_bp_diastolic").removeClass("emptyValue").addClass("value");
                      } else if (obs.concept.uuid === hrConceptUuid) {
                        jq("#last_heart_rate").text(obs.value);
                        jq("#last_heart_rate").removeClass("emptyValue").addClass("value");
                      } else if (obs.concept.uuid === temperatureConceptUuid) {
                        jq("#last_temperature_c").text(obs.value);
                        jq("#last_temperature_c").removeClass("emptyValue").addClass("value");
                      } else if (obs.concept.uuid === respiratoryRateConceptUuid) {
                        jq("#last_respiratory_rate").text(obs.value);
                        jq("#last_respiratory_rate").removeClass("emptyValue").addClass("value");
                      } else if (obs.concept.uuid === o2SatConceptUuid) {
                        jq("#last_o2_sat").text(obs.value);
                        jq("#last_o2_sat").removeClass("emptyValue").addClass("value");
                      }
                    }
                    break;
                  }
                }
              }
        }
    });

    let procedures = [];
    // retrieve all Sterilization procedures for females concept UUIDs
    jq.getJSON(apiBaseUrl + "/concept" + "/" + whSterilizationProcedures, {
          v: 'custom:(id,uuid,display,setMembers:(id,uuid,display)'
        },
        function( data ) {
          if ( data && data.setMembers.length > 0 ) {
              for (let index = 0; index < data.setMembers.length; index++) {
                  let procedure = data.setMembers[index];
                  procedures.push(procedure.uuid);
              }
          }
          if ( procedures.length > 0 ) {
              jq.getJSON(apiBaseUrl + "/obs", {
                  patient: patientUuid,
                  concept: whProcedurePerformed,
                  answers: procedures.join(),
                  v: 'custom:(uuid,display,obsDatetime,value:(uuid,display),concept:(uuid,display,name:(display))'
              },
              function ( data ) {
                    if ( data.results.length > 0 ) {
                        jq("#whProceduresDiv").show();
                        for (let index = 0; index < data.results.length; index++) {
                            let procPerformed = data.results[index];
                            jq("#whProceduresDiv ul").append('<li>').append(' - ').append(procPerformed.value.display).append(' on ').append(new Date(procPerformed.obsDatetime).toDateString()).append('</li>');
                        }
                    }
              });
          }
    });

    // looking for OB/GYN encounters with an obs that indicates that it was an intake encounter
    jq.getJSON(apiBaseUrl + "/encounter", {
            s: 'byObs',
            patient: patientUuid,
            encounterType: obGynEncounterType,
            obsConcept: typeOfVisit,
            obsValues: intakeVisit,
            v: 'custom:(uuid,patient:(uuid),encounterType:(uuid),encounterDatetime,voided,obs:(uuid,display,concept:(uuid,display),obsDatetime,valueCoded:(uuid,display),valueDatetime,valueNumeric,valueText,groupMembers:(uuid,display,person:(uuid),concept:(uuid,display),obsDatetime,valueCoded:(uuid,display),valueDatetime,valueNumeric,valueText,voided),voided)',
            order: 'desc'
          },
          function( data ){
              if (data.results.length > 0) {
                  for (let index = 0; index < data.results.length; index++) {
                    let obGynEnc = data.results[index];
                    let obGynEncDate = new Date(obGynEnc.encounterDatetime);

                    if( (currentEncounterDate.getTime() >=  obGynEncDate.getTime())) {
                        let month = new Intl.DateTimeFormat(undefined, monthOption).format(obGynEncDate);
                        jq("#lastObGynInitialEncounterDate").text(obGynEncDate.getDate() + "-" + month + "-" + obGynEncDate.getFullYear());
                        jq("#lastObGynInitialEncounterTime").text(obGynEncDate.toLocaleTimeString());

                        if (obGynEnc.obs && obGynEnc.obs.length > 0) {
                            for (let j=0; j < obGynEnc.obs.length; j++) {
                                let obs = obGynEnc.obs[j];
                                if (obs.concept.uuid === lastPeriodDateConcept) {
                                    if ( obs.valueDatetime ) {
                                        let periodDate = new Date(obs.valueDatetime);
                                        jq("#obgyn_initial_lastPeriodDate").text(periodDate.getDate() + " " + new Intl.DateTimeFormat(undefined, monthOption).format(periodDate) + " " + periodDate.getFullYear());
                                        jq("#obgyn_initial_lastPeriodDate").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === dueDateConcept) {
                                    if ( obs.valueDatetime ) {
                                        let dueDate = new Date(obs.valueDatetime);
                                        jq("#obgyn_initial_edd").text(dueDate.getDate() + " " + new Intl.DateTimeFormat(undefined, monthOption).format(dueDate) + " " + dueDate.getFullYear());
                                        jq("#obgyn_initial_edd").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === trimAtEnrollConcept) {
                                    if ( obs.valueCoded ) {
                                        switch ( obs.valueCoded.uuid ) {
                                            case firstTrimConcept:
                                                jq("#obgyn_initial_firstTrimester").text("[X] " + obs.valueCoded.display);
                                                jq("#obgyn_initial_firstTrimester").removeClass("emptyValue").addClass("value");
                                                break;
                                            case secondTrimConcept:
                                                jq("#obgyn_initial_secondTrimester").text("[X] " + obs.valueCoded.display);
                                                jq("#obgyn_initial_secondTrimester").removeClass("emptyValue").addClass("value");
                                                break;
                                            case thirdTrimConcept:
                                                jq("#obgyn_initial_thirdTrimester").text("[X] " + obs.valueCoded.display);
                                                jq("#obgyn_initial_thirdTrimester").removeClass("emptyValue").addClass("value");
                                                break;
                                        }
                                    }
                                } else if (obs.concept.uuid === gravidaConcept) {
                                    if ( obs.valueNumeric ) {
                                        jq("#obgyn_initial_gravidaInput").text(Math.round(obs.valueNumeric));
                                        jq("#obgyn_initial_gravidaInput").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === paraConcept) {
                                    if ( obs.valueNumeric ) {
                                        jq("#obgyn_initial_paraInput").text(Math.round(obs.valueNumeric));
                                        jq("#obgyn_initial_paraInput").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === abortusConcept) {
                                    if ( obs.valueNumeric ) {
                                        jq("#obgyn_initial_abortusInput").text(Math.round(obs.valueNumeric));
                                        jq("#obgyn_initial_abortusInput").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === livingConcept) {
                                    if ( obs.valueNumeric ) {
                                        jq("#obgyn_initial_livingInput").text(Math.round(obs.valueNumeric));
                                        jq("#obgyn_initial_livingInput").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === menstruationConcept) {
                                    if ( obs.valueCoded ) {
                                        switch ( obs.valueCoded.uuid ) {
                                            case regularMenstrConcept:
                                                jq("#obgyn_initial_regularMenstruation").text("[X] " + regularTxt);
                                                jq("#obgyn_initial_regularMenstruation").removeClass("emptyValue").addClass("value");
                                                break;
                                            case irregularMenstrConcept:
                                                jq("#obgyn_initial_irregularMenstruation").text("[X] " + irregularTxt);
                                                jq("#obgyn_initial_irregularMenstruation").removeClass("emptyValue").addClass("value");
                                                break;
                                        }
                                    }
                                } else if (obs.concept.uuid === visitDiagConcept) {
                                    if ( obs.groupMembers ) {
                                        for (let index=0; index < obs.groupMembers.length; index++) {
                                            let member = obs.groupMembers[index];
                                            if ( member.concept.uuid === diagConcept && member.valueCoded.uuid === dysmenorConcept ) {
                                                jq("#obgyn_initial_dysmenorrhea").text("[X] " + member.valueCoded.display);
                                                jq("#obgyn_initial_dysmenorrhea").removeClass("emptyValue").addClass("value");
                                                break;
                                            }
                                        }
                                    }
                                } else if (obs.concept.uuid === durationMenstCycleConcept) {
                                    if ( obs.valueNumeric ) {
                                        jq("#obgyn_initial_durationMenstCycle").text(Math.round(obs.valueNumeric));
                                        jq("#obgyn_initial_durationMenstCycle").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === durationMensesConcept) {
                                    if ( obs.valueNumeric ) {
                                        jq("#obgyn_initial_durationMenses").text(Math.round(obs.valueNumeric));
                                        jq("#obgyn_initial_durationMenses").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === ageMenarcheConcept) {
                                    if ( obs.valueNumeric ) {
                                        jq("#obgyn_initial_ageMenarche").text(Math.round(obs.valueNumeric));
                                        jq("#obgyn_initial_ageMenarche").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === age1stSexualInterConcept) {
                                    if ( obs.valueNumeric ) {
                                        jq("#obgyn_initial_age1stSexualInter").text(Math.round(obs.valueNumeric));
                                        jq("#obgyn_initial_age1stSexualInter").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === numSexPartnersConcept) {
                                    if ( obs.valueNumeric ) {
                                        jq("#obgyn_initial_numSexPartners").text(Math.round(obs.valueNumeric));
                                        jq("#obgyn_initial_numSexPartners").removeClass("emptyValue").addClass("value");
                                    }
                                } else if (obs.concept.uuid === pregnancyHistoryConstruct) {
                                    if ( obs.groupMembers ) {
                                        let pregOrder = null;
                                        let typeOfDelivery= null;
                                        let outcome = null;
                                        for (let index=0; index < obs.groupMembers.length; index++) {
                                            let member = obs.groupMembers[index];
                                            if ( member.concept.uuid === pregOrderConcept ) {
                                                pregOrder = Math.round(member.valueNumeric);
                                            } else if ( member.concept.uuid === deliveryTypeConcept ) {
                                                typeOfDelivery = member.valueCoded.display;
                                            } else if ( member.concept.uuid === pregOutcomeConcept ) {
                                                outcome = member.valueCoded.display;
                                            }
                                        }

                                        jq("#obgyn_initial_pregnancyHistoryTable tbody").append('
').append('' + pregOrder + '').append('' + typeOfDelivery + '').append('' + outcome + '').show();
                                    }
                                }
                            }
                        }
                        break;
                    }
                  }

              }
        });

});

ifMode.script:
jq(function() {

var encounterDate = '';
    setUpEdd(
        '',
        ''
    );

    var validateFamHistoryText = function(divElem) {
        var parentDiv = divElem.closest('.medicalHistoryText');

        if (typeof parentDiv !== "undefined" ) {
            var fieldError = parentDiv.find('.field-error');
            var commentsInput= parentDiv.find('input[type=text]');
            if (typeof commentsInput !== "undefined" ) {
                var comments = commentsInput.val();
                if ((typeof comments !=="undefined") && comments.length > 0 ) {
                    if ( parentDiv.find('input:checked').length < 1 ) {
                        if (parentDiv.hasClass("famHistory")) {
                            fieldError.html("").show();
                        } else if (parentDiv.hasClass("patientHistory")) {
                            fieldError.html("").show();
                        }

                    return false;
                    }
                } else {
                    // if there are no comments make sure the adjacent Parent checkbox is not checked
                    if ( parentDiv.find('input:checked').length > 0 ) {
                        fieldError.html("").show();
                        return false;
                    }
                }
            }
            fieldError.html(" ").hide();
        }
        return true;
    }

    jq('.medicalHistoryText').find('input[type=checkbox]').change(function() {
        validateFamHistoryText(jq(this));
    });

    jq('.medicalHistoryText').find('input[type=text]').change(function() {
        validateFamHistoryText(jq(this));
    });

    beforeSubmit.push(function() {
        var numErrors = 0;
        jq('.medicalHistoryText').find('input[type=text]').each(function() {
            if (!validateFamHistoryText(jq(this)) ) {
                ++numErrors;
            }
        });
        return ( numErrors == 0 );
    });

    // handlers for next and submit buttons, see nextAndSubmitButtons.js
    setUpNextAndSubmitButtons();

});
